mapper.select:
SELECT report_id as reportId, access_part as accessPart, trans_month as transMonth, channel_code as channelCode, amount, total_fee as totalFee, hf_fee as hfFee, wpnos.ylcmsb_profit as wpnos.ylcmsbProfit, access_profit as accessProfit, report_time as reportTime FROM codepay_month_report
AND access_part = #{accessPart,jdbcType=VARCHAR}
AND trans_month = #{transMonth,jdbcType=DATE}
AND channel_code = #{channelCode,jdbcType=VARCHAR}
AND trans_month >= DATE_FORMAT(#{startDate,jdbcType=DATE}, '%Y-%m')
AND trans_month <= DATE_FORMAT(#{endDate,jdbcType=DATE}, '%Y-%m')
order by report_time desc

select count(1)
FROM
codepay_month_report


		AND access_part = #{accessPart,jdbcType=VARCHAR}


		AND trans_month = #{transMonth,jdbcType=DATE}


		AND channel_code = #{channelCode,jdbcType=VARCHAR}


		AND trans_month >= DATE_FORMAT(#{startDate,jdbcType=DATE}, '%Y-%m')


		AND trans_month <= DATE_FORMAT(#{endDate,jdbcType=DATE}, '%Y-%m')
